mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}, status = #{status},
id = #{id}

mapper.select:
select setmeal.*
from setmeal

    category_id = #{categoryId}
    and status = #{status}
    and name like concat('%',#{name},'%')

order by price desc
