mapper.update:
update shopping_cart set number = #{number},dish_flavor = #{dishFlavor},create_time = #{createTime}
where
user_id = #{userId}

    and dish_id = #{dishId}


    and setmeal_id = #{setmealId}

mapper.delete:
delete
from shopping_cart
where
user_id = #{userId}

    and dish_id = #{dishId}


    and setmeal_id = #{setmealId}

mapper.select:
Select * from shopping_cart

    user_id = #{userId}

        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}
